import React from "react";
class Converter extends React.Component {
  state = {
    currency: this.props.currencies[0].name,
    value: 0,
  };

  onChange = ({
    target: {
      value,
      dataset: { currency },
    },
  }) => {
    this.setState({
      currency,
      value,
    });
  };

  render() {

    return (
      <div>
        {this.props.currencies.map((n) => (
          <div>
            {n.name}:
            <input value onChange={this.onChange} />
            <input value onChange={this.onChange} />
          </div>
        ))}
      </div>
    );
  }
}
export default Converter;
