import {
  round
} from "mathjs";

function Currencies({ currencies }) {
  return (
    <div>
      {currencies.map((item) => {
        if (item.cc === "EUR") {
          return <li>€{round(item.rate)}</li>;
        }
        if (item.cc === "USD") {
          return <li>${item.rate}</li>;
        }
        if (item.cc === "GBP") {
          return <li>£{item.rate}</li>;
        }
      })}
    </div>
  );
}

export default Currencies;
