function Currencies({currencies}) {
  return (
    <div>
      {currencies.map((item) => {
        //   if (item.cc === "EUR" || item.cc === "USD" || item.cc === "GBP") {
        //     return <div>{item.rate}</div>;
        //   }
        if (item.cc === "EUR"){
            return <li>€{item.rate}</li>;
        }
        if (item.cc === "USD") {
          return <li>${item.rate}</li>;
        }
        if (item.cc === "GBP") {
          return <li>£{item.rate}</li>;
        }
      })}
    </div>
  );
}

export default Currencies;
