import { number } from "mathjs";
import { useState } from "react";
import {useQuery} from 'react-query'
function Converter(){
    function getCurrencies() {
      return fetch(
        "https://bank.gov.ua/NBUStatService/v1/statdirectory/exchange?json"
      )
        .then((response) => response.json())
        .then((responseJson) => {
          return responseJson;
        })
        .catch((error) => {
          console.error(error);
        });
    }
    const { isLoading, data } = useQuery("currencies", () => getCurrencies());
    function getRate(currency){
        return data.find((element) => {
          if (element.cc === currency) {
            return element.rate;
          }
        })[0];}


const [selectedCurrency, setSelectedCurrency] = useState("USD");
 function changeSelectedCurrency(event) {
    setSelectedCurrency(event.target.value);
  }
  const [value1, setValue1] = useState();
  function changeSelectedValue1(event) {
    setValue1(event.target.value);
    setValue2(event.target.value * getRate(selectedCurrency));

  }
   const [value2, setValue2] = useState();
   function changeSelectedValue2(event) {
     setValue2(event.target.value);
   }

    return (
      <div>
        <input onChange={changeSelectedValue1} value={value1} type="number" />
        <select
          name="currency"
          value={selectedCurrency}
          onChange={changeSelectedCurrency}
        >
          <option value="USD">$USD</option>
          <option value="EUR">€EUR</option>
          <option value="UAH">₴UAH</option>
        </select>
        <input onChange={changeSelectedValue2} value={value2} type="number" />
        <select name="currency">
          <option value="USD">$USD</option>
          <option value="GBP">€EUR</option>
          <option value="AUD">₴UAH</option>
        </select>
      </div>
    );
}
export default Converter;
