import classes from "./Converter.module.css";
import { useState } from "react";
import {useQuery} from 'react-query'
import { round } from "mathjs";

function Converter(){
    function getCurrencies() {
      return fetch(
        "https://bank.gov.ua/NBUStatService/v1/statdirectory/exchange?json"
      )
        .then((response) => response.json())
        .then((responseJson) => {
          return responseJson;
        })
        .catch((error) => {
          console.error(error);
        });
    }
    const { isLoading, data } = useQuery("currencies", () => getCurrencies());
    function getRate(currency){
        if (currency==="UAH"){
            return 1;
        }
          return data.find((element) => {
            return element.cc === currency;
          }).rate;}


const [value1, setValue1] = useState();
const [value2, setValue2] = useState();
const [selectedCurrency1, setSelectedCurrency1] = useState("UAH");
const [selectedCurrency2, setSelectedCurrency2] = useState("USD");

 function changeSelectedCurrency1(event) {
    setSelectedCurrency1(event.target.value);
    setValue2(
      round(
        (value1 / getRate(selectedCurrency2)) * getRate(event.target.value),
        2
      )
    );
  }
  function changeSelectedCurrency2(event) {
    setSelectedCurrency2(event.target.value);
    setValue1(
      round(
        (value2 / getRate(selectedCurrency1)) * getRate(event.target.value),
        2
      )
    );
  }
  function changeValue1(event) {
    setValue1(event.target.value);
    setValue2(
      round((event.target.value / getRate(selectedCurrency1)) *
        getRate(selectedCurrency2),2)
    );

  }
   function changeValue2(event) {
     setValue2(event.target.value);
     setValue1(
       round((event.target.value / getRate(selectedCurrency1)) *
         getRate(selectedCurrency2),2)
     );
   }

    return (
      <div className={classes.wrapper}>
        <div>
          <input
            className={classes.currencyInput}
            onChange={changeValue1}
            value={value1}
            type="number"
          />
          <select
            className={classes.currencySelect}
            value={selectedCurrency1}
            onChange={changeSelectedCurrency1}
          >
            <option value="USD">$USD</option>
            <option value="EUR">€EUR</option>
            <option value="UAH">₴UAH</option>
          </select>
        </div>
        <div className={classes.arrow}>
          <svg
            xmlns="http://www.w3.org/2000/svg"
            width="85"
            height="59"
            viewBox="0 0 85 59"
            fill="none"
          >
            <path
              d="M20.8787 58.1213C22.0503 59.2929 23.9497 59.2929 25.1213 58.1213L44.2132 39.0294C45.3848 37.8579 45.3848 35.9584 44.2132 34.7868C43.0416 33.6152 41.1421 33.6152 39.9706 34.7868L23 51.7574L6.02944 34.7868C4.85786 33.6152 2.95837 33.6152 1.7868 34.7868C0.615224 35.9584 0.615224 37.8579 1.7868 39.0294L20.8787 58.1213ZM20 3L20 56H26L26 3H20Z"
              fill="white"
            />
            <path
              d="M64.1213 0.878683C62.9497 -0.292889 61.0503 -0.292889 59.8787 0.878683L40.7868 19.9706C39.6152 21.1421 39.6152 23.0416 40.7868 24.2132C41.9584 25.3848 43.8579 25.3848 45.0294 24.2132L62 7.24264L78.9706 24.2132C80.1421 25.3848 82.0416 25.3848 83.2132 24.2132C84.3848 23.0416 84.3848 21.1421 83.2132 19.9706L64.1213 0.878683ZM65 56V3H59L59 56H65Z"
              fill="white"
            />
          </svg>
        </div>
        <div>
          <input
            className={classes.currencyInput}
            onChange={changeValue2}
            value={value2}
            type="number"
          />
          <select
            className={classes.currencySelect}
            value={selectedCurrency2}
            onChange={changeSelectedCurrency2}
          >
            <option value="USD">$USD</option>
            <option value="EUR">€EUR</option>
            <option value="UAH">₴UAH</option>
          </select>
        </div>
      </div>
    );
}
export default Converter;
