import { number } from "mathjs";
import { useState } from "react";
import {useQuery} from 'react-query'
import { round } from "mathjs";
function Converter(){
    function getCurrencies() {
      return fetch(
        "https://bank.gov.ua/NBUStatService/v1/statdirectory/exchange?json"
      )
        .then((response) => response.json())
        .then((responseJson) => {
          return responseJson;
        })
        .catch((error) => {
          console.error(error);
        });
    }
    const { isLoading, data } = useQuery("currencies", () => getCurrencies());
    function getRate(currency){
        if (currency==="UAH"){
            return 1;
        }
          return data.find((element) => {
            return element.cc === currency;
          }).rate;}


const [value1, setValue1] = useState();
const [value2, setValue2] = useState();
const [selectedCurrency, setSelectedCurrency] = useState("USD");
const [selectedCurrency2, setSelectedCurrency2] = useState("USD");

 function changeSelectedCurrency(event) {
    setSelectedCurrency(event.target.value);
    setValue2(round(
      (value1 / getRate(event.target.value)) * getRate(selectedCurrency2),2)
    );
  }
  function changeSelectedCurrency2(event) {
    setSelectedCurrency2(event.target.value);
    setValue1(
     round( (value2 / getRate(event.target.value)) * getRate(selectedCurrency),2)
    );
  }
  function changeValue1(event) {
    setValue1(event.target.value);
    setValue2(
      round(event.target.value  *
        getRate(selectedCurrency2),2)
    );

  }
   function changeValue2(event) {
     setValue2(event.target.value);
     setValue1(
       round((event.target.value / getRate(selectedCurrency)) *
         getRate(selectedCurrency2),2)
     );
   }

    return (
      <div>
        <input onChange={changeValue1} value={value1} type="number" />
        <select
          name="currency"
          value={selectedCurrency}
          onChange={changeSelectedCurrency}
        >
          <option value="USD">$USD</option>
          <option value="EUR">€EUR</option>
          <option value="UAH">₴UAH</option>
        </select>
        <input onChange={changeValue2} value={value2} type="number" />
        <select value={selectedCurrency2} onChange={changeSelectedCurrency2}>
          <option value="USD">$USD</option>
          <option value="EUR">€EUR</option>
          <option value="UAH">₴UAH</option>
        </select>
      </div>
    );
}
export default Converter;
